// Helper functions for filtering and searching
export const getFoodsByCategory = (category) => {
  return foodDatabase.filter(food => food.category === category);
};

export const getFoodsByDosha = (dosha, effect = 'Balancing') => {
  const doshaKey = `${dosha.toLowerCase()}Effect`;
  return foodDatabase.filter(food => food[doshaKey] === effect);
};

export const searchFoods = (query) => {
  const lowerQuery = query.toLowerCase();
  return foodDatabase.filter(food => 
    food.name.toLowerCase().includes(lowerQuery) ||
    food.tags.some(tag => tag.toLowerCase().includes(lowerQuery))
  );
};

export const filterByTags = (tags) => {
  return foodDatabase.filter(food =>
    tags.some(tag => food.tags.includes(tag))
  );
};

export const categories = [...new Set(foodDatabase.map(food => food.category))];
export const allTags = [...new Set(foodDatabase.flatMap(food => food.tags))];

export default foodDatabase;
